<?php	header('Content-Type: text/html; charset=utf-8');	$_path = realpath ('../') . "/var/cache/prod";	removeCache($_path);	function removeCache($_directory) {		if (is_dir($_directory)) {			$_objects = scandir($_directory);			foreach ($_objects as $_object) {				if ($_object != "." && $_object != "..") {					if (filetype($_directory . "/" . $_object) == "dir")                        removeCache($_directory . "/" . $_object);					else						unlink($_directory . "/" . $_object);				}			}			reset($_objects);			rmdir($_directory);			echo "Dossier <b>" . $_directory . "</b> supprim√©<br>";	   	}	}?>